package com.parakram.assignment1_week5

import android.content.Intent
import android.os.Build
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.View
import android.widget.Toolbar
import androidx.annotation.RequiresApi
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.google.android.material.floatingactionbutton.FloatingActionButton
import com.parakram.assignment1_week5.adapter.PostAdapter
import com.parakram.assignment1_week5.adapter.UsersAdapter
import com.parakram.assignment1_week5.modules.Post
import com.parakram.assignment1_week5.modules.Users
import de.hdodenhof.circleimageview.CircleImageView

class DashboardActivity : AppCompatActivity() {
    private lateinit var imgProfile: CircleImageView
    private lateinit var rvStory: RecyclerView
    private lateinit var rvPosts: RecyclerView
    private lateinit var fabNewPost: FloatingActionButton

    private var arrUser = arrayListOf<Users>()
    private var arrUserNew = arrayListOf<Users>()
    private var postDetails = arrayListOf<Post>()

    private lateinit var userName: String
    private lateinit var profileLink: String
    private lateinit var intent1: Intent

    @RequiresApi(Build.VERSION_CODES.LOLLIPOP)
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_dashboard)

        val toolbar = findViewById<View>(R.id.myToolbar) as Toolbar
        setActionBar(toolbar)

        imgProfile = findViewById(R.id.imgProfile)
        rvStory = findViewById(R.id.rvStory)
        rvPosts = findViewById(R.id.rvPosts)
        fabNewPost = findViewById(R.id.fabNewPost)

        if (intent != null) {
            profileLink = intent.getStringExtra("profileLink").toString()
            Glide.with(this)
                    .load(profileLink)
                    .into(imgProfile)

            userName = intent.getStringExtra("username").toString()
            arrUser = intent.getSerializableExtra("user") as ArrayList<Users>
            arrUserNew = intent.getSerializableExtra("user") as ArrayList<Users>

            for (i in arrUserNew.indices){
                if(userName == arrUserNew[i].username){
                    arrUserNew.remove(arrUserNew[i])
                    break
                }
            }

            val adapter = UsersAdapter(arrUserNew, this@DashboardActivity, 2)
            val linearLayoutManager =
                    LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false)
            rvStory.layoutManager = linearLayoutManager
            rvStory.adapter = adapter


            loadPosts(userName, profileLink)
            if(intent.hasExtra("postDetails")){
                val postDetails = intent.getSerializableExtra("postDetails") as ArrayList<Post>

                val adapter1 = PostAdapter(postDetails, this@DashboardActivity)
                rvPosts.layoutManager = LinearLayoutManager(this@DashboardActivity)
                rvPosts.adapter = adapter1
            }else{
                val adapter1 = PostAdapter(postDetails, this@DashboardActivity)
                rvPosts.layoutManager = LinearLayoutManager(this@DashboardActivity)
                rvPosts.adapter = adapter1
            }
        }

        fabNewPost.setOnClickListener {
            intent1 = Intent(this, CreatepostActivity::class.java)
            intent1.putExtra("userName", userName)
            intent1.putExtra("profileLink", profileLink)
            intent1.putExtra("user", arrUser)
            intent1.putExtra("post", postDetails)
            startActivity(intent1)
        }
    }

    private fun loadPosts(username: String, profileLink: String) {
        postDetails.add(Post(username,
                profileLink,
                "data:image/jpeg;base64,",
                "Killua Zoldyck (キルア゠ゾルディック, Kirua Zorudikku) is the third child of Silva and Kikyo Zoldyck and the heir of the Zoldyck Family, until he runs away from home and becomes a Rookie Hunter. He is the best friend of Gon Freecss and is currently traveling with Alluka Zoldyck."
        ))
        postDetails.add(Post(username,
                profileLink,
                "data:image/jpeg;base64,",
                "Killua Zoldyck (キルア゠ゾルディック, Kirua Zorudikku) is the third child of Silva and Kikyo Zoldyck and the heir of the Zoldyck Family, until he runs away from home and becomes a Rookie Hunter. He is the best friend of Gon Freecss and is currently traveling with Alluka Zoldyck."
        ))
    }
}




